DROP DATABASE IF EXISTS sep;
CREATE DATABASE sep;
USE sep;


  CREATE TABLE campaign (
    campaign_id 				BIGINT AUTO_INCREMENT PRIMARY KEY,
    title 						NVARCHAR(300),
    slug						VARCHAR(400),
    description 				TEXT,
    thumbnail 					NVARCHAR(255),
    created_at 					DATETIME,
    updated_at 					DATETIME,
    is_active  					BOOLEAN 
);



CREATE TABLE project (
    project_id 					BIGINT AUTO_INCREMENT PRIMARY KEY,
    campaign_id 				BIGINT,
    code						VARCHAR(20),
    title 						NVARCHAR(300),
    slug						VARCHAR(400),
    background 					TEXT,
    address						NVARCHAR(255),	
    ward						NVARCHAR(100),
    district					NVARCHAR(100),
    province					NVARCHAR(100),
    total_budget 				DECIMAL(20,2),
    amount_needed_to_raise 		DECIMAL(20,2),
    status 						INT CHECK (status >= 1 AND status <= 4),
    created_at 					DATETIME,
    updated_at 					DATETIME,
    FOREIGN KEY (campaign_id) REFERENCES campaign(campaign_id)
);




CREATE TABLE budget (
    budget_id 					BIGINT AUTO_INCREMENT PRIMARY KEY,
    project_id 					BIGINT,
    title 						NVARCHAR(250),
    unit_price 					DECIMAL(20,2),
	note						NVARCHAR(500),
    status  					INT CHECK (status >= 1 AND status <= 4),
    created_at 					DATETIME,
    updated_at 					DATETIME,
	FOREIGN KEY (project_id) REFERENCES project(project_id)
);




CREATE TABLE role (
    role_id 					BIGINT AUTO_INCREMENT PRIMARY KEY,
    role_name 					NVARCHAR(50),
    role_description 			NVARCHAR(255),
    created_at 					DATETIME,
    updated_at 					DATETIME,
    is_active 					BOOLEAN

);


CREATE TABLE account (
    account_id 					BIGINT AUTO_INCREMENT PRIMARY KEY,
    role_id 					BIGINT,
    code						VARCHAR(20),
    refer_code					VARCHAR(20),
    email 						VARCHAR(50),
    password 					VARCHAR(255),
    fullname 					NVARCHAR(255),	
    gender						INT CHECK (gender >= 0 AND gender <= 1),
    phone 						VARCHAR(20),
    address						NVARCHAR(500),
    description					NVARCHAR(500),
    avatar 						NVARCHAR(255),
    dob 						DATE,
    created_at 					DATETIME,
    updated_at 					DATETIME,
    is_active 					BOOLEAN,
	FOREIGN KEY (role_id) REFERENCES role(role_id)
);




CREATE TABLE assign  (
    assign_id 					BIGINT AUTO_INCREMENT PRIMARY KEY,
    project_id 					BIGINT,
    account_id 					BIGINT,
    created_by 					BIGINT,
    created_at 					DATETIME,
    updated_by 					BIGINT,
    updated_at 					DATETIME,
	FOREIGN KEY (project_id) REFERENCES project(project_id),
	FOREIGN KEY (account_id) REFERENCES account(account_id)

);



CREATE TABLE project_image (
    project_image_id 			BIGINT AUTO_INCREMENT PRIMARY KEY,
    project_id 					BIGINT,
	image 						VARCHAR(255),
	FOREIGN KEY (project_id) REFERENCES project(project_id)
);


CREATE TABLE related_file (
    related_file_id 			BIGINT AUTO_INCREMENT PRIMARY KEY,
    project_id 					BIGINT,
    file 						VARCHAR(255),
	FOREIGN KEY (project_id) REFERENCES project(project_id)
);


CREATE TABLE construction (
    construction_id 			BIGINT AUTO_INCREMENT PRIMARY KEY,
    project_id 					BIGINT,
    title						NVARCHAR(200),
    quantity					INT,
    unit						NVARCHAR(20),
    note						NVARCHAR(200),
    created_at					DATETIME,
    updated_at					DATETIME,
	FOREIGN KEY (project_id) REFERENCES project(project_id)
);

CREATE TABLE challenge (
    challenge_id 				BIGINT AUTO_INCREMENT PRIMARY KEY,
    challenge_code 				VARCHAR(20),
    title						NVARCHAR(255),
    slug 						VARCHAR(355),
    thumbnail					NVARCHAR(255),
    content						TEXT,
    goal						DECIMAL(20,2),
    created_by					BIGINT,
    created_at					DATETIME,
    finished_at					DATETIME
);


CREATE TABLE challenge_project (
    challenge_project_id 		BIGINT AUTO_INCREMENT PRIMARY KEY,
    challenge_id 				BIGINT,
	project_id 					BIGINT,
	FOREIGN KEY (project_id) REFERENCES project(project_id)
);



CREATE TABLE donation (
    donation_id 				BIGINT AUTO_INCREMENT PRIMARY KEY,
    id							BIGINT,
    tid							VARCHAR(50),
    project_id 					BIGINT,
    refer_id					BIGINT,
    transferred_project_id	    BIGINT,
    challenge_id				BIGINT,
    created_by					BIGINT,
    created_at					DATETIME,
    value						DECIMAL(20,2),
    description					NVARCHAR(500),
    bank_sub_acc_id				VARCHAR(50),
    bank_name					NVARCHAR(50),
    corresponsive_name			VARCHAR(100),
    corresponsive_account	 	VARCHAR(50),
    corresponsive_bank_id		VARCHAR(50),
	corresponsive_bank_name		VARCHAR(50),
    note						NVARCHAR(500),
	FOREIGN KEY (project_id) REFERENCES project(project_id),
    FOREIGN KEY (refer_id) REFERENCES account(account_id),
    FOREIGN KEY (created_by) REFERENCES account(account_id),
	FOREIGN KEY (challenge_id) REFERENCES challenge(challenge_id),	
	FOREIGN KEY (transferred_project_id) REFERENCES project(project_id)	
);


CREATE TABLE wrong_donation (
 wrong_donation_id 				BIGINT AUTO_INCREMENT PRIMARY KEY,
 donation_id					BIGINT,
 FOREIGN KEY (donation_id) REFERENCES donation(donation_id)	
);

CREATE TABLE category (
    category_id 				BIGINT AUTO_INCREMENT PRIMARY KEY,
    title 						NVARCHAR(100),
    slug						VARCHAR(200),
    description 				NVARCHAR(255),
    created_at 					DATETIME,
    updated_at 					DATETIME,
    is_active 					BOOLEAN
);


CREATE TABLE news (
    news_id 					BIGINT AUTO_INCREMENT PRIMARY KEY,
    category_id 				BIGINT,
    title 						VARCHAR(250),
    slug						VARCHAR(200),
    thumbnail 					VARCHAR(255),
    short_description 			NVARCHAR(500),
    content 					TEXT,
    created_by 					BIGINT,
    created_at					DATETIME,
    updated_by 					BIGINT,
    updated_at 					DATETIME,
    status 						INT CHECK (status >= 1 AND status <= 4),
	FOREIGN KEY (category_id) REFERENCES category(category_id)

);



CREATE TABLE tracking (
    tracking_id 				BIGINT AUTO_INCREMENT PRIMARY KEY,
    project_id 					BIGINT,
    title 						NVARCHAR(300),
    content 					TEXT,
    date						DATE,
    created_at 					DATETIME,
    updated_at 					DATETIME,
	FOREIGN KEY (project_id) REFERENCES project(project_id)
);



CREATE TABLE tracking_image (
    tracking_image_id 			BIGINT AUTO_INCREMENT PRIMARY KEY,
    tracking_id 				BIGINT,
	image 						VARCHAR(255),
	FOREIGN KEY (tracking_id) REFERENCES tracking(tracking_id)
);


CREATE TABLE expense (
    expense_id 					BIGINT AUTO_INCREMENT PRIMARY KEY,
    project_id 					BIGINT,
    title 						NVARCHAR(250),
    unit_price 					DECIMAL(10,2),
    created_at 					DATETIME,
    updated_at 					DATETIME,
	FOREIGN KEY (project_id) REFERENCES project(project_id)
);


CREATE TABLE expense_file (
    expense_file_id 			BIGINT AUTO_INCREMENT PRIMARY KEY ,
    expense_id 					BIGINT,
	file 						NVARCHAR(255),
	FOREIGN KEY (expense_id) REFERENCES expense(expense_id)
);



CREATE TABLE sponsor (
    sponsor_id 					BIGINT AUTO_INCREMENT PRIMARY KEY ,
    project_id 					BIGINT,
    logo 						VARCHAR(255),
    company_name				NVARCHAR(255),
    business_field				NVARCHAR(100),
    representative				NVARCHAR(255),
    representative_email		VARCHAR(100),
    phone_number				VARCHAR(20),
    value 						DECIMAL(20,2),
    note					    NVARCHAR(500),
    contract					VARCHAR(255),
    created_at 					DATETIME,
    updated_at 					DATETIME,
	FOREIGN KEY (project_id) REFERENCES project(project_id)

);

