INSERT INTO sep.project (campaign_id, code, title, slug, background, address, ward, district, province, total_budget, amount_needed_to_raise, status, created_at, updated_at) VALUES (3, 'DA001', N'Điểm Trường Bản Xa Mang - Trường Mn Sơn Điện 1 - Bản Bun - Xã Sơn Điện- Huyện Quan Sơn - Tỉnh Thanh Hóa', 'da001-iem-truong-ban-xa-mang---truong-mn-son-ien-1---ban-bun---xa-son-ien--huyen-quan-son---tinh-thanh-hoa', '<ol><li><strong>THÔNG TIN ĐIỂM TRƯỜNG CHÍNH</strong></li></ol><figure class="table"><table style="background-color:rgb(255, 255, 255);"><tbody><tr><td style="border:0px solid inherit;">1.&nbsp;&nbsp;&nbsp; Tên trường:</td><td style="border:0px solid inherit;">Trường MN Sơn Điện 1</td></tr><tr><td style="border:0px solid inherit;">2.&nbsp;&nbsp;&nbsp; Địa chỉ điểm trường chính:</td><td style="border:0px solid inherit;">Bản Bun, xã Sơn Điện, huyện Quan Sơn, tỉnh Thanh Hóa</td></tr><tr><td style="border:0px solid inherit;">3.&nbsp;&nbsp;&nbsp; Số điện thoại và email liên hệ (Hiệu trưởng)</td><td style="border:0px solid inherit;">0386539369</td></tr><tr><td style="border:0px solid inherit;">4.&nbsp;&nbsp;&nbsp; Số điện thoại và email liên hệ (Giáo viên chủ nhiệm)</td><td style="border:0px solid inherit;">0965849625</td></tr><tr><td style="border:0px solid inherit;">5.&nbsp;&nbsp;&nbsp; Số điện thoại và email liên hệ: (đoàn)</td><td style="border:0px solid inherit;">0962810567</td></tr><tr><td style="border:0px solid inherit;">6.&nbsp;&nbsp;&nbsp; Số điện thoại và email liên hệ: (trưởng bản)</td><td style="border:0px solid inherit;">0825812988</td></tr><tr><td style="border:0px solid inherit;">7.&nbsp;&nbsp;&nbsp; Tổng số học sinh toàn trường?</td><td style="border:0px solid inherit;">180</td></tr><tr><td style="border:0px solid inherit;">8.&nbsp;&nbsp;&nbsp; Trường có bao nhiêu điểm trường?</td><td style="border:0px solid inherit;">3</td></tr><tr><td style="border:0px solid inherit;">9.&nbsp;&nbsp;&nbsp; Điểm trường chính có bao nhiêu học sinh?</td><td style="border:0px solid inherit;">89</td></tr></tbody></table></figure><ol><li><strong>THÔNG TIN ĐIỂM TRƯỜNG CẦN X Y DỰNG</strong></li></ol><figure class="table"><table style="background-color:rgb(255, 255, 255);"><tbody><tr><td style="border:0px solid inherit;">1.&nbsp;&nbsp;&nbsp; Tên điểm trường:</td><td style="border:0px solid inherit;">Điểm trường bản Xa Mang</td></tr><tr><td style="border:0px solid inherit;">2.&nbsp;&nbsp;&nbsp; Số lượng phòng học hiện có và diện tích mỗi phòng</td><td style="border:0px solid inherit;">Có 2 phòng học tôn tấm (mỗi phòng 25 m2).&nbsp;</td></tr><tr><td style="border:0px solid inherit;">3.&nbsp;&nbsp;&nbsp; Tình trạng hiện nay của phòng học</td><td style="border:0px solid inherit;">Phòng học hiện đã xuống cấp vách tường làm bằng tôn tấm, khung bằng sắt đã bị gỉ sét và cột nhà bằng sắt bị ô xy hoá theo thời gian; mùa hè nóng, mùa đông lạnh học sinh phải nghỉ học dài ngày.</td></tr><tr><td style="border:0px solid inherit;">4.&nbsp;&nbsp;&nbsp; Phòng học được xây từ năm:</td><td style="border:0px solid inherit;">Phòng học tạm XD năm 2017</td></tr><tr><td style="border:0px solid inherit;">5.&nbsp;&nbsp;&nbsp; Trường đã có điện/nước chưa?</td><td style="border:0px solid inherit;">Đã có điện, có nước</td></tr><tr><td style="border:0px solid inherit;">6.&nbsp;&nbsp;&nbsp; Cơ sở vật chất mong muốn được hỗ trợ:</td><td style="border:0px solid inherit;">Xây dựng 2 phòng lớp học có nhà vệ sinh khép kín. Kèm theo các thiết bị như: Bàn ghế, tủ góc, bảng từ, tủ đựng tư trang cá nhân học sinh, sạp ngủ…</td></tr><tr><td style="border:0px solid inherit;">7.&nbsp;&nbsp;&nbsp; Diện tích mỗi phòng cần hỗ trợ:</td><td style="border:0px solid inherit;">mỗi phòng học 35m2 và 02 phòng nhà vệ sinh</td></tr><tr><td style="border:0px solid inherit;">8.&nbsp;&nbsp;&nbsp; Điểm trường hiện đang có bao nhiêu lớp? Là các lớp nào? mỗi lớp có bao nhiêu học sinh?</td><td style="border:0px solid inherit;"><p>* Điểm trường bản Xa Mang hiện có 2 nhóm, lớp.</p><p>Gồm: - 1 nhóm trẻ: 8 cháu (dưới 3 tuổi)</p><p>&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp;&nbsp; - 1 lớp mẫu giáo ghép: 12 cháu</p><p>* Tổng số học sinh khu Xa Mang là: 20</p></td></tr><tr><td style="border:0px solid inherit;">9.&nbsp;&nbsp;&nbsp; Lịch học của các em học sinh (học vào những ngày nào? Giờ học?):</td><td style="border:0px solid inherit;"><p>&nbsp; Học từ thứ 2 đến thứ 6:</p><p><i>- Mùa đông:</i></p><p>+ Sáng từ 7h30-11h30\', chiều từ 13h30-16h.</p><p><i>- Mùa hè:</i></p><p>+ Sáng từ 7h00-11h30, chiều từ 14h30\'-16h30\'</p></td></tr><tr><td style="border:0px solid inherit;">10. Số lượng giáo viên đang giảng dạy tại điểm trường:</td><td style="border:0px solid inherit;">2 giáo viên đang dạy</td></tr><tr><td style="border:0px solid inherit;">11.Giáo viên ở tại điểm trường hay sáng đi chiều về? Có nhà công vụ không?</td><td style="border:0px solid inherit;">GV sáng đi tối về vì không có nhà công vụ dành riêng cho GV</td></tr><tr><td style="border:0px solid inherit;">12. Điểm trường cách trường chính bao nhiêu Km?</td><td style="border:0px solid inherit;">12 km</td></tr><tr><td style="border:0px solid inherit;">13. Học sinh ở cách điểm trường trung bình bao nhiêu Km?</td><td style="border:0px solid inherit;">01 km</td></tr><tr><td style="border:0px solid inherit;">14. Học sinh học tại điểm trường thuộc bản nào?</td><td style="border:0px solid inherit;">bản Xa Mang, xã Sơn Điện</td></tr><tr><td style="border:0px solid inherit;">15. Thôn/ bản đó hiện có bao nhiêu hộ dân? Bao nhiêu khẩu?</td><td style="border:0px solid inherit;">Có 34 hộ với 175 khẩu</td></tr><tr><td style="border:0px solid inherit;">16. Học sinh ăn bán trú ở trường hay về nhà ăn cơm trưa?</td><td style="border:0px solid inherit;">Học sinh ăn bán trú tại trường</td></tr><tr><td style="border:0px solid inherit;">17. Nếu học sinh ăn trưa ở trường thì các em mang cơm đi hay được nhà nước hỗ trợ?</td><td style="border:0px solid inherit;">Học sinh ăn cơm trưa ở trường, mỗi tháng các em đóng 17.000đ/ngày, giáo viên tự nấu cơm trưa và một bữa phụ buổi chiều cho trẻ ăn.</td></tr><tr><td style="border:0px solid inherit;">18. Học sinh thuộc dân tộc thiểu số nào?</td><td style="border:0px solid inherit;">Dân tộc Thái và dân tộc Mường</td></tr><tr><td style="border:0px solid inherit;">19. Nhà trường đã từng xin nhà nước hỗ trợ xây dựng lại điểm trường chưa? Nếu đã xin rồi tại sao vẫn không được đầu tư xây dựng?</td><td style="border:0px solid inherit;">Nhà trường đã xin hỗ trợ nhưng nhà nước hiện tại chưa có chương trình, dự án để hỗ trợ nhà trường XD điểm trường.</td></tr><tr><td style="border:0px solid inherit;">20. Đã có cá nhân/ tổ chức nào liên hệ mong muốn được hỗ trợ xây dựng lại điểm trường chưa?</td><td style="border:0px solid inherit;">Chưa có cá nhân, tổ chức nào đến liên hệ muốn được hỗ trợ XD điểm trường.</td></tr><tr><td style="border:0px solid inherit;">21. Nhà trường có ý định gộp điểm trường phụ vào điểm trường chính không?</td><td style="border:0px solid inherit;">Nhà trường không có ý định gộp điểm trường phụ vào điểm trường chính vì điểm trường phụ ở cách trường chính 12 km, nên phụ huynh không thể đưa hs đi học mỗi ngày.</td></tr></tbody></table></figure><ul><li><strong>THÔNG TIN X Y DỰNG</strong></li></ul><figure class="table"><table style="background-color:rgb(255, 255, 255);"><tbody><tr><td style="border:0px solid inherit;">1.&nbsp;&nbsp;&nbsp; Ô tô có đi được vào điểm trường không hay chỉ có thể đi bằng xe máy? Nếu xây dựng thì có thể vận chuyển nguyên vật liệu bằng gì?&nbsp;</td><td style="border:0px solid inherit;"><p>- Ô tô đi vào đến điểm trường</p><p>- Vận chuyển vật liệu bằng ô tô:</p><p>+ Cát: Từ công ty Quỳnh Phương km 66, xã Mường Mìn vào điểm trường: 45 km.</p><p>+ Đá 1*2: Từ Doanh nghiệp Tiến Loan km 42, Khu Hao, thị trấn Sơn Lư đến điểm trường: 20 km.</p><p>+ Gạch đỏ: Từ huyện Bá Thước vào đến điểm trường: 60 km.</p><p>+ Các vật liệu khác từ thị trấn Sơn Lư vào điểm trường 15 km.</p></td></tr><tr><td style="border:0px solid inherit;">2.&nbsp;&nbsp;&nbsp; Trong trường hợp được tài trợ, trường mới sẽ được xây dựng ở đâu?</td><td style="border:0px solid inherit;">Nếu được tài trợ thì lớp học mới sẽ được xây dựng tại điểm trường cũ vì có nguồn đất và mặt bằng XD.</td></tr><tr><td style="border:0px solid inherit;">3.&nbsp;&nbsp;&nbsp; Vị trí lớp học sẽ xây dựng đã có giấy tờ sử dụng đất chưa?</td><td style="border:0px solid inherit;">Nhà trường đã có giấy tờ sổ đỏ được nhà nước cấp.</td></tr></tbody></table></figure>', N'Trường Mn Sơn Điện 1 - Bản Bun - Xã Sơn Điện- Huyện Quan Sơn - Tỉnh Thanh Hóa', N'Xã Sơn Điện', N'Huyện Quan Sơn', N'Tỉnh Thanh Hóa', 400000000.00, 400000000.00, 2, '2024-08-19 21:53:37', '2024-08-19 21:53:37');
