INSERT INTO sep.project (campaign_id, code, title, slug, background, address, ward, district, province, total_budget, amount_needed_to_raise, status, created_at, updated_at) VALUES (1, 'DA006', N'Nhà Hạnh Phúc 165- Bùi Khánh Ly - Phố Côm, Thị Trấn Vụ Bản, Huyện Lạc Sơn, Tỉnh Hòa Bình', 'da006-nha-hanh-phuc-165--bui-khanh-ly---pho-com-thi-tran-vu-ban-huyen-lac-son-tinh-hoa-binh', '<p style="margin-left:0px;">&nbsp;</p><p style="text-align:center;"><strong>PHIẾU THÔNG TIN NGÔI NHÀ HẠNH PHÚC</strong></p><figure class="table"><table><tbody><tr><td style="border:0px solid inherit;">1</td><td style="border:0px solid inherit;">Họ và tên học sinh</td><td style="border:0px solid inherit;"><p>Bùi Khánh Ly, sinh năm 2010</p><p>Bùi Ngọc Thi, Sinh năm 2020</p></td></tr><tr><td style="border:0px solid inherit;">2</td><td style="border:0px solid inherit;">Dân tộc</td><td style="border:0px solid inherit;">Mường</td></tr><tr><td style="border:0px solid inherit;">3</td><td style="border:0px solid inherit;">Địa chỉ lưu trú hiện nay</td><td style="border:0px solid inherit;">Phố Côm, thị trấn Vụ Bản, huyện Lạc Sơn, tỉnh Hòa Bình</td></tr><tr><td style="border:0px solid inherit;">4</td><td style="border:0px solid inherit;">Học sinh lớp, trường?</td><td style="border:0px solid inherit;"><p style="text-align:justify;">HS lớp 8 trường TH&amp;THCS Liên vũ, là học sinh ngoan ngoãn.</p></td></tr><tr><td style="border:0px solid inherit;">5</td><td style="border:0px solid inherit;"><p style="margin-left:0mm;">Ước mơ của em bé:</p></td><td style="border:0px solid inherit;"><p style="margin-left:0mm;text-align:justify;">Sau này muốn làm Bác sĩ để chữa bệnh cho người người nghèo.</p></td></tr><tr><td style="border:0px solid inherit;">6</td><td style="border:0px solid inherit;">Đầu mối liên hệ</td><td style="border:0px solid inherit;"><p style="text-align:justify;">Đại diện gia đình học sinh: Bùi Thị Can</p><p>Đại diện Tỉnh đoàn/Hội liên hiệp thanh niên Việt Nam tỉnh.SĐT:</p><p>Đại diện chính quyền địa phương: 0943999588</p></td></tr></tbody></table></figure><p>I.THÔNG TIN EM BÉ</p><p>HOÀN CẢNH GIA ĐÌNH</p><figure class="table"><table><tbody><tr><td style="border:0px solid inherit;">1</td><td style="border:0px solid inherit;">Em bé mồ côi bố hay mẹ?</td><td style="border:0px solid inherit;">Em bé mô côi bố</td></tr><tr><td style="border:0px solid inherit;">2</td><td style="border:0px solid inherit;">Đang ở cùng ai hiện tại?</td><td style="border:0px solid inherit;">Em đang sống cùng mẹ.</td></tr><tr><td style="border:0px solid inherit;">3</td><td style="border:0px solid inherit;">Tuổi của tất cả người thân, ở cùng?</td><td style="border:0px solid inherit;"><p>Bà:Bùi Thị Chù 76 tuổi</p><p>Mẹ: em năm nay 50 tuổi</p><p>em : Bùi Ngọc Thi 4 tuổi</p></td></tr><tr><td style="border:0px solid inherit;">4</td><td style="border:0px solid inherit;">Hoàn cảnh gia đình</td><td style="border:0px solid inherit;">Em có nhà rồi nhưng nhà ở tại bằng gỗ, tranh nứa hiện tại nhà đang xuống cấp và rách nát, diện tích nhà ở trong nhà k có gì giá trị.</td></tr><tr><td style="border:0px solid inherit;">5</td><td style="border:0px solid inherit;">Thu nhập chính của gia đình</td><td style="border:0px solid inherit;">Hiện tại nguồn thu nhập chính của gia đình mẹ đi làm thuê và hôm nào được nghỉ học 2 chị em đi bắt cua để bán.</td></tr><tr><td style="border:0px solid inherit;">6</td><td style="border:0px solid inherit;">Hỗ trợ từ nhà nước</td><td style="border:0px solid inherit;"><p style="text-align:justify;">Gia đình thuộc diện hộ nghèo trong nhiều năm hiện nay gia đình em không có chế độ ưu đãi nào của nhà nước hoặc địa phương.</p></td></tr></tbody></table></figure><p>III. ĐỀ XUẤT X Y DỰNG</p><figure class="table"><table><tbody><tr><td style="border:0px solid inherit;">1</td><td style="border:0px solid inherit;">Địa điểm xây dựng</td><td style="border:0px solid inherit;">Đất đứng tên mẹ tại Phố Côm, thị trấn Vụ Bản, huyện Lạc Sơn, tỉnh Hòa Bình</td></tr><tr><td style="border:0px solid inherit;">2</td><td style="border:0px solid inherit;">Nếu xây nhà, em ở cùng ai?</td><td style="border:0px solid inherit;">Nếu xây nhà em ở mẹ và bà và em</td></tr><tr><td style="border:0px solid inherit;">3</td><td style="border:0px solid inherit;">Đối ứng của địa phương(huyện/xã/đoàn/thôn…) hoặc gia đình.</td><td style="border:0px solid inherit;"><p style="margin-left:0mm;text-align:justify;">Gia đình và địa phương ,Đoàn Thanh niên thị trấn hỗ trợ đào móng, xây cho đến khi hoàn thiện.</p></td></tr><tr><td style="border:0px solid inherit;">4</td><td style="border:0px solid inherit;"><p style="margin-left:0mm;">Thửa đất xây dựng có sổ đỏ cho gia đình em chưa?</p></td><td style="border:0px solid inherit;"><p style="margin-left:0mm;">Thửa đất có sổ đỏ</p></td></tr><tr><td style="border:0px solid inherit;">5</td><td style="border:0px solid inherit;"><p style="margin-left:0mm;">Từ trung tâm thị trấn đến địa điểm thi công là bao nhiêu km?</p></td><td style="border:0px solid inherit;"><p style="margin-left:0mm;text-align:justify;">Từ trung tâm xã đến địa điểm thi công là 4 km</p></td></tr><tr><td style="border:0px solid inherit;">6</td><td style="border:0px solid inherit;"><p style="margin-left:0mm;">Địa điểm thi công có thế vận chuyển vật liệu bằng ô tô hay chỉ bằng xẻ máy? Nếu vận chuyển xemáy thì bao nhiêu m/km? Có đường bê tông cho ô tô không?</p></td><td style="border:0px solid inherit;"><p style="margin-left:0mm;">Đường bê tông Ô tô có thể vào tận công trình.</p></td></tr><tr><td style="border:0px solid inherit;">7</td><td style="border:0px solid inherit;"><p style="margin-left:0mm;">Điều kiện thời tiết địa phương (lũ lụt/sạt lở…) có thuận lợi thi công không?</p></td><td style="border:0px solid inherit;"><p style="margin-left:0mm;">Thời tiết thuận lợi cho thi công từ tháng 01 đến tháng 9.</p></td></tr><tr><td style="border:0px solid inherit;">8</td><td style="border:0px solid inherit;"><p style="margin-left:0mm;">Đề xuất (kinh phí, diện tích, số phòng, vật liệu..)</p></td><td style="border:0px solid inherit;"><p style="margin-left:0mm;">Tổng kinh phí 80 triệu VNĐ</p><p style="margin-left:0mm;">Xây nhà ở kiểu cấp 4, có diện tích 50m2,</p><p style="margin-left:0mm;">Vật liệu xây dựng gạch sắt thép, bê tông.</p></td></tr></tbody></table></figure><p>&nbsp;</p><p style="margin-left:16px;"><img class="image_resized" style="aspect-ratio:1920/2560;height:auto;width:688px;" src="https://firebasestorage.googleapis.com/v0/b/savvy-serenity-424116-g1.appspot.com/o/uploads%2F2024%2F06%2Fz5497573495381_d9cb20f1d1da19f810be58d04cbc5ed2.jpg?alt=media&amp;token=a0ad1fee-c20d-4584-a38c-1ecaf81d6b41" alt="z5497573495381_d9cb20f1d1da19f810be58d04cbc5ed2" width="1920" height="2560"></p><p style="margin-left:0px;">z5497573495381_d9cb20f1d1da19f810be58d04cbc5ed2</p>', N'Phố Côm, Thị Trấn Vụ Bản, Huyện Lạc Sơn, Tỉnh Hòa Bình', N'Thị trấn Vụ Bản', N'Huyện Lạc Sơn', N'Tỉnh Hoà Bình', 80000000.00, 20000000.00, 3, '2024-08-19 22:36:37', '2024-08-19 22:36:37');



