INSERT INTO sep.news (category_id, title, slug, thumbnail, short_description, content, created_by, created_at, updated_by, updated_at, status) VALUES (3, '(báo Giao Thông) Chuyện Trung “đồng Nát” Xây Trường, Nuôi Trẻ Vùng Cao', '4-bao-giao-thong-chuyen-trung-ong-nat-xay-truong-nuoi-tre-vung-cao', 'news/thumbnail/4/1c33c69b-540d-44bc-9c4c-3e3a1d10a551_hoang-hoa-trung-ben-cac-em-nho-vung-cao-1587050871-width1004height565 (1).jpeg', N'(báo Giao Thông) Chuyện Trung “đồng Nát” Xây Trường, Nuôi Trẻ Vùng Cao', '<p style="margin-left:0px;"><span style="color:rgb(51,51,51);">Cảm ơn Báo Giao Thông đã lặn lội tới tận nhà, ngồi lên cái ghế nhặt ở bãi rác của em để nghe em trò chuyện tới hơn 3 tiếng đồng hồ .</span><br><br><span style="color:rgb(51,51,51);">Thêm những câu chuyện mới được khai thác, lộ mức thu nhập mình đang có, thật ra mới chỉ có từ tháng 2/2020 thôi, và cũng là 1 khoản dự phòng cho việc duy trì team .</span><br><br><span style="color:rgb(51,51,51);">Phỏng vấn anh </span><a href="https://www.facebook.com/que.huong.9235?__cft__[0]=AZX8kKoCTSRM5ohfOUY1ePq06wsbg-E5GwPiqVOqdTSgx-8Aa_-nfhZBUzCe43RYvXAjBS_dSMBx_d_QlkCKkAwFlL4qALJt-vFcQwGYAgbzt4A2eGvqRV3MQtm0fv82kV_udCDwHkeCc9R7EpTmd1Zs&amp;__tn__=-]K-R"><span style="color:rgb(51,51,51);"><u>Bui Van An</u></span></a><span style="color:rgb(51,51,51);"> - chuyên viên PGD Nậm Pồ với cô giáo cắm bản nè.</span><br><br><span style="color:rgb(51,51,51);">Mời cả nhà đọc bài báo heng, </span><a href="https://www.facebook.com/sucmanh2000/"><span style="color:rgb(51,51,51);"><u>Sức mạnh 2000 - Tiền lẻ mỗi ngày Xây ngay nghìn trường mới</u></span></a><span style="color:rgb(51,51,51);"> &nbsp;lên sóng tiếp tục nè: </span><a href="https://sucmanh2000.com/"><span style="color:rgb(51,51,51);"><u>https://sucmanh2000.com/</u></span></a></p><p><span style="color:rgb(51,51,51);">Trên MoMo mới có 49 người đăng ký góp 2000 mỗi năm, buồn ghê ta, chắc là do mới lên được phiên bả cho Android, chưa lên phiên bản cho Iphone nè.</span></p><p><spa